export default class BluetoothDevice {
    constructor({ deviceId, connectionToken, deviceName, rssi, manufacturerSpecificData }: {
        deviceId: any;
        connectionToken: any;
        deviceName: any;
        rssi: any;
        manufacturerSpecificData: any;
    }, dispatcher: any);
    connectionToken: any;
    rssi: any;
    manufacturerSpecificData: any;
    id: any;
    name: any;
    dispatcher: any;
    gatt: BluetoothRemoteGATTServer;
    watchingAdvertisements: boolean;
    watchAdvertisements(): Promise<any>;
    unwatchAdvertisements(): void;
    addEventListener(eventName: any, callback: any): void;
    removeEventListener(eventName: any, callback: any): void;
}
import BluetoothRemoteGATTServer from "./BluetoothRemoteGATTServer";
